mapper.select:
SELECT HOTEL_NO, ROOM_NO, BI_PRICE, BS_DATE, BE_DATE, JUN_PRICE, JS_DATE, JE_DATE, SUNG_PRICE, SS_DATE, SE_DATE FROM ( SELECT HOTEL_NO, ROOM_NO, BI_PRICE, BS_DATE, BE_DATE, JUN_PRICE, JS_DATE, JE_DATE, SUNG_PRICE, SS_DATE, SE_DATE, ROW_NUMBER() OVER(PARTITION BY HOTEL_NO ORDER BY BI_PRICE ASC) AS RN FROM ROOMPRICE_T ) WHERE RN = 1 AND HOTEL_NO
#{item.hotelNo}
ORDER BY DECODE(HOTEL_NO,
#{item.hotelNo}, ${index}

mapper.update:
UPDATE HOTEL_T
   SET HIT = HIT + 1
 WHERE HOTEL_NO = #{hotelNo}

mapper.select:
WITH PRICE AS( SELECT HOTEL_NO, MIN(BI_PRICE) AS MB FROM ROOMPRICE_T GROUP BY HOTEL_NO ) SELECT A.RN, A.MB, A.HOTEL_NO, A.REGION_NO, A.HOTEL_NAME, A.HOTEL_ADDRESS, A.LATITUDE, A.LONGITUDE, A.HOTEL_DETAIL, A.PHONE_NUMBER, A.H_EMAIL, A.CREATED_AT, A.MODIFIED_AT, A.HIT, A.STATUS, A.RECOMMEND_STATUS, A.THUMBNAIL, A.FILESYSTEM_NAME, A.IMAGE_PATH FROM ( SELECT H.HOTEL_NO, H.REGION_NO, H.HOTEL_NAME, H.HOTEL_ADDRESS, H.LATITUDE, H.LONGITUDE, H.HOTEL_DETAIL, H.PHONE_NUMBER, H.H_EMAIL, H.CREATED_AT, H.MODIFIED_AT, H.HIT, H.STATUS, H.RECOMMEND_STATUS, HI.THUMBNAIL, HI.FILESYSTEM_NAME, HI.IMAGE_PATH, P.MB, ROW_NUMBER() OVER(
ORDER BY P.MB ASC
ORDER BY P.MB DESC
) AS RN FROM HOTEL_T H LEFT JOIN HOTEL_IMAGE_T HI ON H.HOTEL_NO = HI.HOTEL_NO LEFT JOIN REGION_T R ON H.REGION_NO = R.REGION_NO LEFT JOIN PRICE P ON H.HOTEL_NO = P.HOTEL_NO WHERE HI.THUMBNAIL = 1 ) A WHERE A.RN BETWEEN #{begin} AND #{end}

   SELECT HOTEL_NO, ROOM_NO, BI_PRICE, BS_DATE, BE_DATE, JUN_PRICE, JS_DATE, JE_DATE, SUNG_PRICE, SS_DATE, SE_DATE
     FROM ROOMPRICE_T
     WHERE

    	 HOTEL_NO = #{hotelNo} 


		 	 ROOM_NO = #{roomNo}

SELECT ROOM_NO, HOTEL_NO, ROOM_DETAIL, ROOM_NAME, ROOM_MANY, R_VIEW, BLEAKFAST, SMOKE, PEOPLE, BED, SHOWER, R_SIZE
  FROM ROOMTYPE_T 
 WHERE HOTEL_NO = #{hotelNo}

SELECT ROOM_NO, HOTEL_NO, ROOM_DETAIL, ROOM_NAME, ROOM_MANY, R_VIEW, BLEAKFAST, SMOKE, PEOPLE, BED, SHOWER, R_SIZE
  FROM ROOMTYPE_T
 WHERE ROOM_NO = #{roomNo}


   SELECT NVL(CNT,0)
  	 FROM ( SELECT COUNT(*) AS CNT
				      FROM RESERVE_T
				     WHERE (RESERVE_START = #{checkin}
				        OR (RESERVE_START <  #{checkin} AND RESERVE_FINISH > #{checkin})
				        OR (RESERVE_START >  #{checkin} AND RESERVE_START < #{checkout}))
				       AND ROOM_NO = #{roomNo}
				     )

mapper.insert:
INSERT INTO REVIEW_T (
    REVIEW_NO
  , HOTEL_NO
  , USER_NO
  , REVIEW_CONTENTS
  , REGIST_AT
  , STAR
) VALUES (
    REVIEW_SEQ.NEXTVAL
  , #{hotelNo}
  , #{userDto.userNo}
  , #{reviewContents}
  , TO_CHAR(SYSDATE,'YYYY/MM/DD')
  , #{star}
)

mapper.select:
SELECT COUNT(*)
  FROM REVIEW_T
 WHERE HOTEL_NO = #{hotelNo}

SELECT AVG(STAR) AS AVERAGE_RATING
  FROM REVIEW_T
 WHERE HOTEL_NO = #{hotelNo}

SELECT RESERVE_NO, USER_NO
  FROM RESERVE_T
 WHERE HOTEL_NO = #{hotelNo}
