mapper.select:
SELECT COUNT(*)
  FROM RESERVE_T
 WHERE USER_NO = #{userNo}

mapper.insert:

  SELECT RESERVE_SEQ.NEXTVAL 
    FROM DUAL

INSERT INTO RESERVE_T (
    RESERVE_NO
  , RESERVED_AT
  , REQUESTED_TERM
  , AGREE
  , DEPARTURE_LOC
  , RESERVE_STATUS
  , RESERVE_START
  , RESERVE_FINISH
  , RESERVE_PERSON
  , RESERVE_PRICE
  , USER_NO
  , PACKAGE_NO
) VALUES (
    #{reserveNo}
  , TO_CHAR(SYSDATE, 'YYYYMMDD') 
  , #{requestedTerm}
  , #{agree}
  , #{departureLoc}
  , #{reserveStatus}
  , 

            #{reserveStart}


            NULL


  , 

            #{reserveFinish}


            NULL


  , #{reservePerson}
  , #{reservePrice}
  , #{userDto.userNo}
  , #{packageDto.packageNo}  
)


  SELECT TOURIST_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO TOURIST_T (
    TOURIST_NO
  , NAME
  , BIRTH_DATE
  , GENDER
  , MOBILE
  , AGE_CASE
  , RESERVE_NO
) VALUES (
    #{touristNo}
  , #{name}
  , #{birthDate}
  , #{gender}
  , #{mobile}
  , #{ageCase}
  , #{reserveDto.reserveNo}
)


  SELECT PAYMENT_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO PAYMENT_T (
    PAYMENT_NO
  , IMP_UID
  , PAY_YN
  , PAY_METHOD
  , PAID_AMOUNT
  , PAID_AT
  , MERCHANT_UID
  , BUYER_NAME
  , BUYER_EMAIL
  , ERROR_MSG
  , PAY_STATUS
    RESERVE_NO
) VALUES (
    #{paymentNo}
  , #{impUid}  
  , #{payYn}  
  , #{payMethod}  
  , #{paidAmount}  
  , #{paidAt}  
  , #{merchantUid}  
  , #{buyerName}  
  , #{buyerEmail}  
  , #{errorMsg}  
  , #{payStatus}  
  , #{reserveDto.reserveNo}
)

mapper.update:
UPDATE RESERVE_T
   SET REQUESTED_TERM = #{requestedTerm}
     , DEPARTURE_LOC = #{departureLoc}
 WHERE RESERVE_NO = #{reserveNo}
